#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on 2017/6/21 下午6:32@author: zhechengma"""import pandas as pdimport pymysqlfrom setting import *class MyselfDB(object):	def __init__(self,host=HOST,port=PORT,user=USER,password=PASSWORD,db=DB,charset=CHARSET):		self.conn=pymysql.connect(host=host,port=port,password=password,user=user,db=db,charset=charset)	def select(self,talename):		SQL="select id,name from {} where creation_date ='0'".format(talename)		data1=pd.read_sql(SQL,con = self.conn)		return data1	def update(self,name,date):		cur=self.conn.cursor()		cur.execute("insert into add_date_class (name,date) values ('{}','{}')".format(name,date))		self.conn.commit()